import React, { useState } from "react";
import { fetchStockData } from "../services/api";

const StockPortfolio = () => {
  const [symbols, setSymbols] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetchStockData = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetchStockData(symbols);
      setData(response);
    } catch (err) {
      setError("Failed to fetch stock data.");
    }
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white rounded-xl shadow-md space-y-4">
      <h1 className="text-2xl font-bold text-center">Stock Portfolio Manager</h1>
      <input
        type="text"
        placeholder="Enter stock symbols (e.g., AAPL, TSLA)"
        className="w-full p-2 border rounded"
        value={symbols}
        onChange={(e) => setSymbols(e.target.value)}
      />
      <button
        onClick={handleFetchStockData}
        className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        disabled={loading}
      >
        {loading ? "Loading..." : "Get Stock Data"}
      </button>
      {error && <p className="text-red-500 text-center">{error}</p>}
      {data && (
        <div className="mt-4 p-4 border rounded bg-gray-100">
          <h2 className="text-lg font-bold">Stock Prices</h2>
          <ul>
            {Object.entries(data).map(([symbol, stockInfo]) => (
              <li key={symbol} className="mt-2">
                <strong>{symbol}:</strong> {JSON.stringify(stockInfo)}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default StockPortfolio;
